import{_ as a,e}from"./app.622ab2be.js";var i="/tech-blog/assets/jpa_basic_1.3ee1130a.png";const l={},r=e('<h1 id="jpa-java-persistence-api" tabindex="-1"><a class="header-anchor" href="#jpa-java-persistence-api" aria-hidden="true">#</a> JPA (Java Persistence Api)</h1><h3 id="jpa-java-persistence-api-1" tabindex="-1"><a class="header-anchor" href="#jpa-java-persistence-api-1" aria-hidden="true">#</a> JPA (Java Persistence Api)?</h3><ul><li>Java \uC9C4\uC601 ORM \uAE30\uC220\uD45C\uC900</li><li>JPA \uB294 \uAD6C\uD604\uCCB4\uAC00 \uC544\uB2C8\uACE0 \uC778\uD130\uD398\uC774\uC2A4\uC758 \uBAA8\uC74C</li><li>JPA \uC778\uD130\uD398\uC774\uC2A4\uB97C \uAD6C\uD604\uD55C ORM \uD504\uB808\uC784\uC6CC\uD06C\uAC00 \uBCC4\uB3C4 \uC874\uC7AC (\uB300\uD45C\uC801\uC73C\uB85C Hibernate)</li></ul><h3 id="orm-object-relational-mapping" tabindex="-1"><a class="header-anchor" href="#orm-object-relational-mapping" aria-hidden="true">#</a> ORM (Object Relational Mapping)?</h3><ul><li>\uAC1D\uCCB4\uC640 \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uB97C \uB9E4\uD551</li><li>\uAC1D\uCCB4\uC640 \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uAC01\uC790 \uC124\uACC4\uD558\uACE0 ORM \uD504\uB808\uC784\uC6CC\uD06C\uAC00 \uB9E4\uD551</li><li>\uAC1D\uCCB4\uC640 \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uD328\uB7EC\uB2E4\uC784\uC758 \uBD88\uC77C\uCE58\uB97C \uD574\uACB0</li></ul><h3 id="jpa-\u1109\u1161\u110B\u116D\u11BC-\u1109\u1175-\u110C\u1161\u11BC\u110C\u1165\u11B7" tabindex="-1"><a class="header-anchor" href="#jpa-\u1109\u1161\u110B\u116D\u11BC-\u1109\u1175-\u110C\u1161\u11BC\u110C\u1165\u11B7" aria-hidden="true">#</a> JPA \uC0AC\uC6A9 \uC2DC \uC7A5\uC810</h3><ul><li><p>\uD2B9\uC815 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0 \uC885\uC18D\uB418\uC9C0 \uC54A\uB294\uB2E4</p><ul><li>JDBC \uAC00 DB \uC5D0\uAC8C \uC9C8\uC758\uD558\uAE30 \uC804, JPA \uAC00 \uC0AC\uC6A9 DB \uC5D0 \uC54C\uB9DE\uAC8C SQL \uC744 \uC791\uC131\uD558\uC5EC, \uAC1C\uBC1C\uB3C4\uC911 DB \uAC00 \uBC14\uB00C\uC5B4\uB3C4 \uBB38\uC81C\uC5C6\uC774 \uB3D9\uC77C\uD55C \uB3D9\uC791\uC744 \uBCF4\uC7A5\uD55C\uB2E4.</li></ul><p><img src="'+i+'" alt="JPA_BASIC"></p></li><li><p>SQL \uC911\uC2EC \uAC1C\uBC1C -&gt; \uAC1D\uCCB4 \uC911\uC2EC \uAC1C\uBC1C</p><ul><li>SQL \uC791\uC131\uC744 JPA \uAC00 \uB300\uC2E0 \uD574\uC90C\uC73C\uB85C\uC368, \uBE44\uC988\uB2C8\uC2A4 \uB85C\uC9C1\uC5D0 \uC880 \uB354 \uC9D1\uC911\uC801\uC73C\uB85C \uAC1C\uBC1C\uC744 \uD560 \uC218 \uC788\uB2E4. \uC774\uB85C\uC778\uD558\uC5EC \uC0DD\uC131\uC0B0\uC774 \uC99D\uAC00\uD55C\uB2E4.</li></ul></li><li><p>\uD328\uB7EC\uB2E4\uC784\uC758 \uBD88\uC77C\uCE58 \uD574\uACB0</p><ul><li>\uAC1D\uCCB4\uC9C0\uD5A5 \uC5D0\uC11C\uB294 \uCEA1\uC220\uD654, \uC0C1\uC18D, \uB2E4\uD615\uC131 \uAC19\uC740 \uAC1C\uB150\uC774 \uC874\uC7AC\uD558\uC9C0\uB9CC, \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC5D0\uC11C\uB294 \uC774\uB7EC\uD55C \uAC1C\uB150\uB4E4\uC774 \uC874\uC7AC\uD558\uC9C0 \uC54A\uB294\uB2E4. \uC774\uB807\uAC8C \uC11C\uB85C \uC9C0\uD5A5\uD558\uB294 \uBAA9\uC801\uC774 \uB2EC\uB77C\uC11C \uB370\uC774\uD130\uB97C \uD45C\uD604\uD558\uB294 \uBC29\uBC95\uB3C4 \uB2E4\uB974\uB2E4. \uC774\uB7EC\uD55C \uBB38\uC81C\uB294 \uAC1D\uCCB4\uC640 \uAD00\uACC4\uD615 \uB370\uC774\uD130\uBCA0\uC774\uC2A4\uC758 \uD328\uB7EC\uB2E4\uC784\uC758 \uBD88\uC77C\uCE58\uB77C \uD55C\uB2E4. \uC774\uB7EC\uD55C \uBB38\uC81C\uB97C JPA \uAC00 \uAC1D\uCCB4\uC640 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC0AC\uC774\uC5D0\uC11C \uD328\uB7EC\uB2E4\uC784\uC744 \uD574\uACB0\uD574\uC900\uB2E4.</li></ul></li></ul><p><em>\uCC38\uACE0. \uC790\uBC14 ORM \uD45C\uC900 JPA \uD504\uB85C\uADF8\uB798\uBC0D</em></p>',8);function t(p,n){return r}var c=a(l,[["render",t],["__file","index.html.vue"]]);export{c as default};
